mapper.update:
update tbl_question
set `gmt_modify` = now()

    , `a_chooser` = #{aChooser}


    , `b_chooser` = #{bChooser}


    , `c_chooser` = #{cChooser}


    , `d_chooser` = #{dChooser}


    , `correct_rate` = #{correctRate}


    , `select_times` = #{selectTimes}

where `id` = #{id} and `status` = 1
